mapper.select:
/* 양도 게시글 목록 정렬 조회*/ SELECT tb.transfer_board_no , p.products_image , tb.cust_id , c.cust_nm , pc.middle_category , pc.small_category , tb.transfer_title , tb.rental_fee , tb.individual_grants , tb.transfer_rental_processing_status , tb.register_date FROM customer c RIGHT JOIN transfer_board tb USING (cust_id) INNER JOIN sell_products sp USING (sell_products_no) INNER JOIN products p USING (products_no) INNER JOIN products_category pc ON p.product_category_no = pc.product_category_no ORDER BY
tb.rental_fee ASC,
tb.rental_fee DESC,
DATEDIFF(tb.board_valid_date, NOW()) ASC,
tb.register_date DESC LIMIT 16 OFFSET #{pageable.offset};

/* 양도 게시글 검색 조회 */ SELECT tb.transfer_board_no , p.products_image , tb.cust_id , c.cust_nm , pc.middle_category , pc.small_category , tb.transfer_title , tb.rental_fee , tb.individual_grants , tb.transfer_rental_processing_status , tb.register_date FROM customer c RIGHT JOIN transfer_board tb USING (cust_id) INNER JOIN sell_products sp USING (sell_products_no) INNER JOIN products p USING (products_no) INNER JOIN products_category pc ON p.product_category_no = pc.product_category_no
p.products_nm LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY tb.register_date DESC LIMIT 16 OFFSET #{pageable.offset};
